mapper.update:
update bao_auth
set `name` = #{name,jdbcType=CHAR},
  passwork = #{passwork,jdbcType=CHAR},
  userId = #{userid,jdbcType=CHAR}
where uuid = #{uuid,jdbcType=VARCHAR}

mapper.select:
select uuid, `name`, passwork, userId
from bao_auth

  and `name` = #{name}
  and passwork = #{passwork}
  and userid = #{userid}
